import React, { Component } from "react";
import "./index.css";
class Calendar extends Component {
  static defaultProps = {
    date: new Date(),
    years: [
      2010, 2011, 2012, 2013, 2014, 2015, 2016, 2017, 2018, 2019, 2020, 2021,
      2022,
    ],
    monthNames: [
      "Январь",
      "Февраль",
      "Март",
      "Апрель",
      "Май",
      "Июнь",
      "Июль",
      "Август",
      "Сентябрь",
      "Октябрь",
      "Ноябрь",
      "Декабрь",
    ],
    weekDayNames: ["Пн", "Вт", "Ср", "Чт", "Пт", "Сб", "Вc"],
    onChange: Function.prototype,
  };
  constructor(props) {
    super(props);
    this.handlePrevMonthButtonClick =
      this.handlePrevMonthButtonClick.bind(this);
    this.handleNextMonthButtonClick =
      this.handleNextMonthButtonClick.bind(this);
  }
  state = {
    date: this.props.date,
    CurrentDate: new Date(),
    selectedDate: null,
  };
  /*get year() {
    return this.state.date.getFullYear();
  }
  get month() {
    return this.state.date.getMonth();
  }*/
  handlePrevMonthButtonClick() {
    const date = new Date(
      this.state.date.getFullYear(),
      this.state.date.getMonth() - 1
    );
    console.log(date);
    this.setState({ date });
  }
  handleNextMonthButtonClick() {
    const date = new Date(
      this.state.date.getFullYear(),
      this.state.date.getMonth() + 1
    );
    console.log(date);
    this.setState({ date });
  }
  handleSelectedChange() {
    const year = this.yearSelect.value;
    const date = new Date(year, month);
  }
  handleDayClick(date) {
    console.log(date);
    this.setState({ selectedDate: date });

    this.props.onChange(date);
  }
  render() {
    const { years, monthNames, weekDayNames } = this.props;
    const monthData = [
      [
        undefined,
        undefined,
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        new Date(),
      ],
      [
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        new Date(),
      ],
      [
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        new Date(),
      ],
      [
        new Date(),
        new Date(),
        new Date(),
        new Date(),
        undefined,
        undefined,
        undefined,
      ],
    ];
    return (
      <div className="calendar">
        <header>
          <button onClick={this.handlePrevMonthButtonClick}>{"<"}</button>
          <select
            ref={(element) => (this.monthSelect = element)}
            onChange={this.handleSelectedChange}
          >
            {monthNames.map((monthName, index) => (
              <option key={monthName} value={index}>
                {monthName}
              </option>
            ))}
          </select>
          <select
            ref={(element) => (this.yearSelect = element)}
            onChange={this.handleSelectedChange}
          >
            {years.map((yearName) => (
              <option key={yearName} value={yearName}>
                {yearName}
              </option>
            ))}
          </select>
          <button onClick={this.handleNextMonthButtonClick}>{">"}</button>
        </header>
        <table>
          <thead>
            <tr>
              {weekDayNames.map((weekDay) => (
                <td key={weekDay}>{weekDay}</td>
              ))}
            </tr>
          </thead>
          <tbody>
            {monthData.map((week, index) => (
              <tr key={index} className="week">
                {week.map((date, index) =>
                  date ? (
                    <td
                      key={index}
                      className="day"
                      onClick={() => this.handleDayClick(date)}
                    >
                      {date.getDate()}
                    </td>
                  ) : (
                    <td key={index} />
                  )
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Calendar;
