import React, { useState, useRef, useReducer } from "react";
import classnames from "classnames";
export default class Note extends React.Component {
  constructor() {
    super();
    this.state = {
      header: [],
      date: new Date(),
      text: [],
      count: 0,
    };
    this.handleInput = this.handleInput.bind(this);
  }
  handleInput = () => {
    const val = this.headInput.value;
    const text = this.textar.value;

    this.setState((prevState) => ({
      header: [...prevState.header, val],
      text: [...prevState.text, text],
      count: this.state.count + 1,
    }));
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <form
          className={classnames("form", {
            yellow: this.state.count <= 8,
            red: this.state.count > 8,
          })}
        >
          <fieldset>
            <legend>{"Заметки"}</legend>
            <label>{"Заголовок"}</label>
            <input
              type={"text"}
              name={"header"}
              ref={(e) => (this.headInput = e)}
            ></input>
            <label>{"Дата"}</label>
            <input type={"text"} name={"date"}></input>
            <label>{"Содержание"}</label>
            <textarea name="text" ref={(e) => (this.textar = e)}></textarea>
            <button type={"submit"} onClick={this.handleInput}>
              {"analsgin"}
            </button>
          </fieldset>
        </form>
      </div>
    );
  }
}
