import React, { Component, useState, useEffect, useRef } from "react";
import InputMask from "react-input-mask";
export default function StudentInfo(props) {
  const [info, setStudentInfo] = useState({
    FIO: "Default",
    dateOfBirth: 1,
    universityEntranceDate: 1,
    faculty: "",
    group: 1,
    spec: "",
    email: "",
    telNumber: 1,
  });
  const refs = useRef([]);
  const handleSubmit = (e) => {
    e.preventDefault();
    const FIOD = refs?.current[0]?.value;
    const dateOfBirth = refs?.current[1]?.value;
    const entrance = refs?.current[2]?.value;
    const faculty = refs?.current[3]?.value;
    const group = refs?.current[4]?.value;
    const spec = refs?.current[5]?.value;
    const email = refs?.current[6]?.value;
    console.log(FIOD);
    setStudentInfo({ ...info, FIO: FIOD });
    console.log(info.FIO);
  };

  return (
    <>
      <div>
        <form style={{ width: "fit-content" }} onSubmit={handleSubmit}>
          <fieldset>
            <legend>{"Новый Студент"}</legend>
            <label htmlFor="FIO">{"ФИО:"}</label>
            <input
              type={"text"}
              id="FIO"
              ref={(e) => (refs.current[0] = e)}
            ></input>
            <label htmlFor="date">{"Дата рождения:"}</label>
            <InputMask
              mask="99.99.9999"
              id="date"
              ref={(e) => (refs.current[1] = e)}
            ></InputMask>
            <label htmlFor="entrance">{"Год поступления:"}</label>
            <input
              type={"text"}
              id="entrance"
              ref={(e) => (refs.current[2] = e)}
            ></input>
            <label htmlFor="faculty">{"Факультет:"}</label>
            <input
              type={"text"}
              id={"faculty"}
              ref={(e) => (refs.current[3] = e)}
            ></input>
            <label htmlFor="group">{"Группа:"}</label>
            <input
              type={"text"}
              id={"group"}
              ref={(e) => (refs.current[4] = e)}
            ></input>
            <label htmlFor="spec">{"Специальность"}</label>
            <input
              type={"text"}
              id={"spec"}
              ref={(e) => (refs.current[5] = e)}
            ></input>
            <label htmlFor="email">{"Email:"}</label>
            <input
              type={"email"}
              id="email"
              ref={(e) => (refs.current[6] = e)}
            ></input>
            <input type={"Submit"} value={"Submit"}></input>
            {/*номер телефона*/}
          </fieldset>
        </form>
      </div>
    </>
  );
}
